mapper.select:
SELECT *
FROM genFile
WHERE 1
And relTypeCode = #{relTypeCode}
And relId = #{relId}
And typeCode =
#{typeCode}
And type2Code = #{type2Code}
And fileNo = #{fileNo}

SELECT *
FROM genFile
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}

mapper.update:
UPDATE genFile

	updateDate = NOW(),
	relId = #{relId}

WHERE id = #{id}

mapper.delete:
DELETE FROM genFile
WHERE relTypeCode = #{relTypeCode}
AND relId = #{relId}
